import * as accountsAPI from './modules/accountsAPI';
import * as state from './modules/stateManagement';

$( document ).ready(function() {

	let currentState = state.update(accountsAPI);
	if(currentState.loggedInUser.id) {
		// make an api call to display that id's photos
		$(".logout").removeClass("hideElement");
		// REDIRECT TO /GALLERY
		// window.location.assign("/gallery");
	} else {
		$(".login").removeClass("hideElement");
	}

});